uniswap_v3.query:
query GetPools($pageSize: Int!, $lastId: ID!) {
  pools(first: $pageSize, orderBy: id, orderDirection: asc, where: { id_gt: $lastId }) {
    id
    token0 { id }
    token1 { id }
    feeTier
  }
}

velodrome_v2.query:
query GetPools($pageSize: Int!, $lastId: ID!) {
  liquidityPools(first: $pageSize, orderBy: id, orderDirection: asc, where: { id_gt: $lastId }) {
    id
    inputTokens { id }
  }
}
